mapper.select:
SELECT scenario_id, scenario_name FROM scenario

    where project_id = #{ajaxData}

order by scenario_id desc

SELECT scene_id, scene_num FROM scene

    where scenario_id = #{ajaxData}

order by scene_id desc

SELECT project_id, project_name FROM project


    where project_id = #{ajaxData}

order by project_id desc

SELECT day_id, day_title FROM schedule_day


    where project_id = #{ajaxData}

order by project_id desc
