mapper.update:
UPDATE
log
SET
log_num = #{log_num}
, log_date = #{log_date}
, log_content = #{log_content}
, project_id  =  #{project_id}


WHERE
log_id = #{log_id}

mapper.delete:
DELETE FROM logjoin
WHERE company_id = #{company_id}
and log_id = #{log_id}

mapper.select:

SELECT * FROM logjoin
WHERE company_id = #{company_id}
  AND EXTRACT(YEAR FROM log_date) = #{year}
  AND EXTRACT(MONTH FROM log_date) = #{month}
