mapper.insert:
insert into scene values( (select concat('scene', (nvl(max(substr(scene_id, 6)), 0)+1)) from scene), (select nvl(max(scene_num),0)+1 from scene),
#{scene_place}, #{story_board}, #{scene_content}, 'scenario1'
)

mapper.update:
update scene
set scene_num = #{scene_num},
    scene_content = #{scene_content},
    story_board = #{story_board},
    scene_place = #{scene_place}
where scene_id = #{scene_id}

mapper.insert:
insert into actor values(
(select concat('actor', (nvl(max(substr(actor_id, 6)), 0)+1)) from actor),
'scene1', 
'pd1', 
#{actor_info}
)

mapper.update:
update actor
set scene_id = #{scene_id},
actor_info = #{actor_info}
where actor_id = #{actor_id}
