mapper.select:
select count(${id}) as totalPost from ${table}

    where 1=1 


    where company_id = #{company_id} 



    AND table_id like '%${title}%'

select *

from(select rownum as r, t.*


from (select * from ${table}

    where 1=1 


    where company_id = #{company_id} 


    AND table_id like '%${title}%'

order by ${id} desc) t)


where r between #{postStart} and #{postEnd}
